mapper.select:
    select a.*,b.line_name,c.shop_id,c.shop_name,d.fc_id,d.fc_name from prod_box_log a
	left join sys_line b on a.line_id = b.line_id
	left join sys_workshop c on b.shop_id = c.shop_id
	left join sys_factory d on c.fc_id = d.fc_id
	where 1=1

      and a.line_id = #{lineId}


      and c.shop_id = #{shopId}


      and d.fc_id = #{fcId}


		and a.prod_batch_code = #{prodBatchCode}


      and prod_code like '%'+#{prodCode}+'%'


      and prod_number like '%'+#{prodNumber}+'%'


      and prod_tuhao like '%'+#{tuhao}+'%'


      and box_barcode = #{boxCode1}


      and po_code = #{poCode}


      and a.create_time between #{beginTime} and #{endTime}

    order by a.create_time desc
