mapper.select:
    select a.*,b.line_name,c.shop_id,c.shop_name,d.fc_id,d.fc_name,e.prod_number,e.prod_tuhao,e.prod_name from prod_finished_product a 
	left join sys_line b on a.line_id = b.line_id
	left join sys_workshop c on b.shop_id = c.shop_id
	left join sys_factory d on c.fc_id = d.fc_id
	left join plan_order e on a.po_code = e.po_code
	where 1=1

      and a.line_id = #{lineId}


      and c.shop_id = #{shopId}


      and d.fc_id = #{fcId}


      and a.prod_code like '%'+#{prodCode}+'%'


      and e.prod_number like '%'+#{prodNumber}+'%'


      and e.prod_tuhao like '%'+#{tuhao}+'%'


      and box_barcode = #{boxCode1}


      and fp_barcode like '%'+#{barcode}+'%'


      and a.po_code = #{poCode}


      and a.create_time between #{beginTime} and #{endTime}

    order by a.create_time desc

select top 1 a.*,b.line_name,c.shop_id,c.shop_name,d.fc_id,d.fc_name,e.pt_draw prod_tuhao from prod_finished_product a left join sys_line b on a.line_id = b.line_id left join sys_workshop c on b.shop_id = c.shop_id left join sys_factory d on c.fc_id = d.fc_id left join [DBSAP].[SAP].[dbo].[pt_mstr] e on a.prod_code = e.pt_part where 1=1
and a.fp_barcode = #{barcode}
and a.prod_code = #{prodCode}

select top 1 a.*,b.line_name,c.shop_id,c.shop_name,d.fc_id,d.fc_name,e.prod_tuhao,e.prod_name from prod_finished_product a left join sys_line b on a.line_id = b.line_id left join sys_workshop c on b.shop_id = c.shop_id left join sys_factory d on c.fc_id = d.fc_id left join plan_order e on a.po_code = e.po_code where 1=1
and a.line_id = #{lineId}
and a.fp_rfid = #{rfid}
