mapper.select:
select a.*,b.prod_code,b.prod_number from prod_order_stock a left join plan_order b on a.po_code = b.po_code where 1=1
and b.prod_code like '%'+#{prodCode}+'%'
and b.line_id = #{lineId}
and a.po_code = #{poCode}
and b.prod_number like '%'+#{prodNumber}+'%'
and a.mat_code like '%'+#{matProdCode}+'%'
and a.mat_number like '%'+#{matProdNumber}+'%'
and a.box_barcode like '%'+#{boxCode}+'%'
and a.create_time between #{beginTime} and #{endTime}
order by a.update_time desc

select prod_order_stock.po_code, '' procCode, box_barcode, mat_code, mat_name, mat_batch_no, mat_furnace_no, mat_glevel, box_quan_left from prod_order_stock left join plan_order on prod_order_stock.po_code = plan_order.po_code where 1=1
and plan_order.line_id = #{lineId}
and prod_order_stock.po_code = #{poCode}
and mat_code = #{matCode}
and box_barcode = #{boxBarcode}
and is_cleaned = 0 and box_quan_left > 0

select po_code, op_no procCode, box_barcode, mat_code, mat_name, mat_batch_no, mat_furnace_no, mat_glevel, box_quan_left from prod_process_stock where 1=1
and line_id = #{lineId}
and po_code = #{poCode}
and op_no = #{procCode}
and mat_code = #{matCode}
and box_barcode = #{boxBarcode}
and is_cleaned = 0 and box_quan_left > 0

select po_code, prod_box_log.op_no procCode, prod_box_material.mat_box_barcode box_barcode, mat_code, mat_name, mat_batch_no, mat_furnace_no, mat_glevel, prod_box_material.mat_count box_quan_left from prod_box_material left join prod_box_log on prod_box_material.box_barcode = prod_box_log.box_barcode where 1=1
and line_id = #{lineId}
and po_code = #{poCode}
and prod_box_log.op_no = #{procCode}
and mat_code = #{matCode}
and prod_box_material.mat_box_barcode = #{boxBarcode}
and prod_box_log.is_wip = 1 and prod_box_log.is_feeded = 0 and prod_box_log.is_outsource = 0
